SELECT pre.id id, pre.pre_sku preSku, pre.product_name productName, pre.sku sku, anrule.anruleStr anruleStr, anrule.anrule anrule, pre.color color, basic.attribute colorStr, country.country country, pre.sale_qty saleQty, pre.market_capacity marketCapacity, pre.rma_ratio rmaRatio, pre.mp_date mpDate, pre.region saleZone, pre.`status`, pre.remark remark FROM bs_prepare_sku pre LEFT JOIN bs_item_basic_data basic ON basic.`value` = pre.color AND basic.type = 1 LEFT JOIN ( SELECT mapping.skuid skuid, group_concat(basic.attribute) anruleStr, group_concat(basic. VALUE) anrule FROM bs_item_mapping mapping LEFT JOIN bs_item_basic_data basic ON mapping.maping = basic.`value` WHERE mapping.data_type = 1 AND basic.type = 2 GROUP BY mapping.skuid ) anrule ON pre.id = anrule.skuid LEFT JOIN ( SELECT mapping.skuid skuid, group_concat(mapping.maping) country FROM bs_item_mapping mapping WHERE mapping.data_type = 2 GROUP BY mapping.skuid ) country ON country.skuid = pre.id WHERE (1=1)
and model_id = #{model_id}
and pre.status = #{status}
and pre.id = #{id}
and pre.pre_sku = #{preSku}

